  styleUrls: ['./add-car.component.css']
})
export class AddCarComponent {

  carName = ''
  carYear = 2017;
  onAddCar = new EventEmitter<{name: string, year: number}>()

  constructor() {
  }

  addCar() {

    this.carName = '',
      this.carYear = 2017

  }
}
