import { Component } from '@angular/core';

@Component({
  selector: 'app-cars',
  templateUrl: './cars.component.html',
  styleUrls: ['./cars.component.css']
})
export class CarsComponent {


 carName = ''
  addCarStatus = false
  constructor() {

  }

  addCar() {
   this.addCarStatus = true;

  }
}
<input type="text" class="form-control" [(ngModel)]="carName">
<button class = "btn btn-primary" (click)="addCar()">добавит машину</button>
<p *ngIf="!addCarStatus">Машина не добавлена</p>
<p #succStatus>Машина добавлена!</p>

<hr>
<app-car></app-car>
