import { Component } from '@angular/core';

@Component({
  selector: 'app-cars',
  templateUrl: './cars.component.html',
  styleUrls: ['./cars.component.css']
})
export class CarsComponent {


  carName = ''
  carYear = 2017;

  cars: [{name: string, year: number}] = [{
    name: 'Ford',
    year: 2015
  }, {
    name: 'Mazda',
    year: 2010
  }, {
    name: "Audi",
    year: 2017
  }]
  constructor() {

  }

  addCar() {
   this.addCarStatus = true;
   this.cars.push(this.carName);
   this.carName = ''

  }
}
<input type="text" class="form-control" [(ngModel)]="carName">
<input type="text" class="form-control" [(ngModel)]="carYear">
<button class = "btn btn-primary" (click)="addCar()">добавит машину</button>
<p *ngIf="!addCarStatus; else successStatus">Машина не добавлена</p>
<ng-template #successStatus>
  <p>Машина добавлена!</p>
</ng-template>


<hr>

<app-car *ngFor="let car of cars"></app-car>
