import {Component} from '@angular/core';
import {CarsServise} from "./cars.servise";

interface Cars {
  name: string,
  color: string,
  id: number
};

@Component({
  selector: 'app-root',
  templateUrl: `app.component.html`,
  styleUrls: ['./app.component.css']
})
export class AppComponent {

  cars: Cars[] = [];

  constructor(private carsService: CarsServise) { }


  loadCars() {
    this.carsService
      .getCars()
      //.subscribe((cars: Cars[] ) => {

        //console.log(cars)
       // this.cars = cars
     // })
  }
}
<div class="col-xs-8 col-xs-offset-2">
  <button class="btn btn-primary" (click)="loadCars()">Загрузить</button>
  <hr>
  <ul class="list-group">
    <li class="list-group-item" *ngFor="let car of cars">
      <span class="badge">{{car.id}}</span>{{car.name}} {{car.color}}
    </li>
  </ul>
</div>
