import { Component, Input} from "@angular/core";
import { ConsoleService } from "../console.service";


@Component({
  selector: 'app-car',
  templateUrl: `./car.component.html`,
  styleUrls: ['./car.component.css']
})

export class CarComponent {
@Input() car: any;


  getClass() {
    return {
      'list-group-item-success': !this.car.isSold,
      'list-group-item-danger': this.car.isSold,
      'list-group-item': true
    }
  }

  onAction(type: string) {
    this.car.isSold = type === 'buy' ? true : false;
    console.log(`${this.car.name} status = ${type}`)
  }
}
<div [ngClass]="getClass()">
  <h4 [ngStyle]="{marginButton: '20px'}">{{ car.name }}</h4>
  <div>
    <button
      class="btn btn-sm btn-primary"
      [disabled]="car.isSold"
      (click)="onAction('buy')"
    >Купить</button>
    <button
      class="btn btn-sm btn-default"
      [disabled]="!car.isSold"
      (click)="onAction('return')"
      >Вернуть</button>
  </div>
</div>
