import {Component} from '@angular/core';
import {divTtigger} from "./app.animation";


@Component({
  selector: 'app-root',
  templateUrl: `app.component.html`,
  styleUrls: ['./app.component.css'],
  animations: [divTtigger]
})
export class AppComponent {
  isVisible = false;
}
<button class="btn btn-primary" (click)="isVisible = !isVisible">Переключить</button>
<hr>

<div
  [@divTrigger]="isVisible"
  class="alert alert-danger"
  *ngIf="isVisible"
>
  Lerem ipson
</div>
