import {Component, ElementRef, EventEmitter, Output, ViewChild} from '@angular/core';

@Component({
  selector: 'app-add-car',
  templateUrl: './add-car.component.html',
  styleUrls: ['./add-car.component.css']
})
export class AddCarComponent {
  get carYearInput(): ElementRef {
    return this._carYearInput;
  }

  set carYearInput(value: ElementRef) {
    this._carYearInput = value;
  }

  carName = ''
  carYear = 2017;
  @Output('onAddCar') carEmiter = new EventEmitter<{name: string, year: number}>()
  @ViewChild('carYearInput') carYearInput: ElementRef
  constructor() {
  }

  addCar(carNameEl: HTMLInputElement) {
  console.log(this._carYearInput)
    this.carEmiter.emit({
      name: carNameEl.value,
      year: this.carYear
    })

    carNameEl.value = '',
      this.carYear = 2017

  }
}
