@Component({
  selector: 'app-root',
  template: '<div class="col-xs-8 col-xs-offset-2">' +
    '<h2>{{ num }}</h2>' +
    '<h2>{{ num | appPow:3:"=" }}</h2>' +
    '</div> '
  //styleUrls: ['./app.component.css']
})
export class AppComponent {
  cars = [
    {name: 'Ford'},
    {name: 'Mazda'},
    {name: 'Bently'},
    {name: 'Audi'},
    {name: 'Mercedes'},
    {name: ''}
  ]
}
