import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  headerText = 'Создание своей дерективы';
  items = [1, 2, 3, 4, 5]
}
<div class="container">
  <div class="col-xs-8 col-xs-offset-2">

    <h3 [appBackground]="'red'" [defaultColor]="'blue'">{{headerText}}</h3>

    <ul class="list-group">
      <li *ngFor="let item of items" (click)="onClick(item)" class="list-group-item"></li>
    </ul>


  </div>
</div>
