import {Component} from '@angular/core';
import {divTrigger} from "./app.animation";
import {
  ensureSourceFileVersions
} from "@angular-devkit/build-angular/src/builders/browser-esbuild/angular/angular-host";


@Component({
  selector: 'app-root',
  templateUrl: `app.component.html`,
  styleUrls: ['./app.component.css'],
  animations: [divTrigger]
})
export class AppComponent {
  isVisible = false;

  onAnimationStart(event: AnimationEvent) {
    console.log('Start: ' event)
  }

  onAnimationDone() {

  }
}
<button class="btn btn-primary" (click)="isVisible = !isVisible">Переключить</button>
<hr>

<div
  @divTrigger
  (@divTrigger.start)="onAnimationStart($event)"
  (@divTrigger.done)="onAnimationDone($event)"
  class="alert alert-danger"
  *ngIf="isVisible"
>
  Lerem ipson
</div>
