@Component({
  selector: 'app-car',
  templateUrl: './car.component.html',
  styleUrls: ['./car.component.css']
})
export class CarComponent  {

 // @ts-ignore
  @Input('carItem') car: {name: string, year: number};

  private carItem = false


}
