import { Component } from '@angular/core';

@Component({
  selector: 'app-cars',
  templateUrl: './cars.component.html',
  styleUrls: ['./cars.component.css']
})
export class CarsComponent {

 addCarStatus = "";
 inputText = ''
  constructor() {

  }

  addCar() {
   this.addCarStatus = 'машина добавлена'
  }

  onKeyUp(event: Event) {
   //console.log(event);
    this.inputText = ''
  }
}
<button class = "btn btn-primary" (click)="addCar()">добавит машину</button>
<p>{{ addCarStatus }}</p>
<hr>
<app-car></app-car>
<app-car></app-car>

<hr>

<input type="text" class="form-control" (keyup)="onKeyUp($event)" )>
<p>{{ inputText }}</p>
