import {Component} from '@angular/core';

@Component({
  selector: 'app-cars',
  templateUrl: './cars.component.html',
  styleUrls: ['./cars.component.css']
})
export class CarsComponent {




    cars : [{ year: number; name: string }, { year: number; name: string }, { year: number; name: string }] = [{
    name: 'Ford',
    year: 2015
  }, {
    name: 'Mazda',
    year: 2010
  }, {
    name: "Audi",
    year: 2017
  }]
  constructor() {

  }
  updateCarList(car: {name: string, year: number}) {
    this.cars.push(car)
  }

}
<p>Форма добавление машин</p>
<hr>
<app-add-car (onAddCar)="updateCarList($event)"></app-add-car>
<hr>

<div class="list-group">
  <app-car class="list-group-item" *ngFor="let car of cars" [carItem]="car">
    <div class="alert al"></div>
  </app-car>
</div>
