import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  template: '<div class="col-xs-8 col-xs-offset-2">' +
    '<h2>{{ name }}</h2>' +
    '<h2>{{ name | lowercase }}</h2>' +
    '<h2>{{ name | uppercase }}</h2>' +
    '<h2>{{ name | slice:0:3}}</h2>' +
    '<h2>{{ name | slice:3:6 | uppercase }}</h2>' +
    '<hr>' +
    '<h2>{{ pl }}</h2>' +
    '<h2>{{ pl | number }}</h2>' +
    '<h2>{{ pl | number:"1.3-4" }}</h2>' +
    '<h2>{{ pl | number:"2.2-2" }}</h2>' +
    '<hr>' +
    '<h2>{{ money | currency:"EUR": false}}</h2>' +
    '<hr>' +
    '<h2></h2>' +
    '</div> '
  //styleUrls: ['./app.component.css']
})
export class AppComponent {
  name = 'WebForMyself';
  pl = Math.PI;
  money = 350;
  date = new Date();
  amount = 0.45;
  object = {
    foo: 'bar',
    buz: 'qux',
    nested: {
      xyz: 3,
      numbers: [1, 2, 3]
    }
  };
}
