import { Component } from '@angular/core';

@Component({
  selector: 'app-cars',
  templateUrl: './cars.component.html',
  styleUrls: ['./cars.component.css']
})
export class CarsComponent {

 addCarStatus = "";
 inputText = 'Default text'
  constructor() {

  }

  addCar() {
   this.addCarStatus = 'машина добавлена'
  }

  onKeyUp({event}: { event: any }) {
   //console.log(event);
      this.inputText = event.target.value
  }
}
<button class = "btn btn-primary" (click)="addCar()">добавит машину</button>
<p>{{ addCarStatus }}</p>
<hr>
<app-car></app-car>
<app-car></app-car>

<hr>

<input type="text" class="form-control" (keyup.enter)="onKeyUp({event : $event})">
<input type="text" class="form-control" [(ngModel)]="inputText">

<p>{{ inputText }}</p>
