import {Component, OnInit} from '@angular/core';
import {FormControl, FormGroup, Validators} from "@angular/forms";


@Component({
  selector: 'app-root',
  templateUrl: `app.component.html`,
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit{

  answers = [{
    type: 'yes',
    text: 'да'
  }, {
    type: 'no',
    text: 'нет'
  }];

  // @ts-ignore
  form: FormGroup;

  ngOnInit() {

    this.form = new FormGroup({
      user: new FormGroup({
        email:  new FormControl('', [Validators.required, Validators.email]),
        pass: new  FormControl('', [Validators.required, this.checkForLength])
      }),
      country: new FormControl('ru'),
      answer: new FormControl('no')
    })
  }

  onSubmit() {
    console.log('submited', this.form)
  }

  checkForLength(control: FormControl) {
    if (control.value.length <= 4) {
      return {
        'lengthError': true
      };
    }
    return null
  }
}
<div class="col-xs-8 col-xs-offset-2">
  <form [formGroup]="form" (ngSubmit)="onSubmit()">
    <div formGroupName="user">
      <div class="form-group" [ngClass]="{'has-error': form.get('user.email')?.invalid && form.get('user.email')?.touched}">
        <label>Email</label>
        <input type="text" class="form-control" formControlName="email">
        <p *ngIf="form.get('user.email')?.invalid && form.get('user.email')?.touched">введите корректный эмейл</p>
      </div>
      <div class="form-group" [ngClass]="{'has-error': form.get('user.pass')?.invalid && form.get('user.pass')?.touched}">
        <label>Пароль</label>
        <input type="password" class="form-control" formControlName="pass">
        <p *ngIf="form.get('user.pass')?.invalid && form.get('user.pass')?.touched">Пароль не должен быть пустым!</p>
      </div>
    </div>
      <div class="form-group">
        <label>Выберете страну</label>
        <select class="form-control" formControlName="country">
          <option value="ru">Россия</option>
          <option value="by">Белорусь</option>
          <option value="ua">Украина</option>
        </select>
      </div>
    <div class="radio" *ngFor="let ans of answers">
      <label>
        <input
          type="radio"
          [value]="ans.type"
          name="answer"
          formControlName="answer"
        >{{ ans.text}}
      </label>
    </div>
    <button
      class="btn btn-success"
      type="submit"
      [disabled]="form.invalid"
    >Сохранит</button>
  </form>
</div>
