import {Component, OnInit} from '@angular/core';
import {CarsServise} from "./cars.servise";

interface Cars {
  name: string;
  color: string;
  id: number;
};

@Component({
  selector: 'app-root',
  templateUrl: `app.component.html`,
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {

  color = [
    'red',
    'blue',
    'green',
    'pink',
    'yellow',
    'grey'
  ]
  cars: Cars[] = [];
  carName: string = '';
  appTitle = '';

  constructor(private carsService: CarsServise) { }

  ngOnInit() {
  }

  loadCars() {
    this.carsService
      .getCars()
      .subscribe((cars ) => {
         console.log(cars)
         // @ts-ignore
        this.cars = cars
       },
        (error) => {
          alert(error)
        })
  }

  addCar() {

    // @ts-ignore
    this.carsService.addCar(this.carName).subscribe((car: Cars) => {
      this.cars.push(car)
    });
    this.carName = ''
  }

  getRandColor() {
    const num = Math.round(Math.random() * (this.color.length - 1));
    return this.color[num]
  }

  setNewColor(car: Cars) {
    this.carsService.changeColor(car, this.getRandColor())
      .subscribe((data) => {
        console.log(data)
      })
  }

  deleteCar(car: Cars) {
    this.carsService.deleteCar(car)
      .subscribe((data) => {
        this.cars = this.cars.filter(c => c.id !== car.id)
      })
  }
}
<div class="col-xs-8 col-xs-offset-2">
  <input type="text" class="form-control" [(ngModel)]="carName">
  <hr>
  <button class="btn btn-primary" (click)="loadCars()">Загрузить</button>
  <button class="btn btn-success" (click)="addCar()">Добавить машину</button>
  <hr>
  <ul class="list-group">
    <li class="list-group-item" *ngFor="let car of cars">
      <span class="badge" [ngStyle]="{background: car.color}">{{car.id}}</span>
      {{car.name}}
      <button class="btn btn-xs btn-info" (click)="setNewColor(car)">Изменить цвет</button>
      <button class="btn btn-xs btn-danger" (click)="deleteCar(car)">Удалить машину</button>
    </li>
  </ul>
</div>
