import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  template: '<div class="col-xs-8 col-xs-offset-2">' +
    '<input type="text" class="form-control" [(ngModel)]="searchCar")]>' +
    '<hr>' +
    '<ul class="list-group">' +
      '<li class="list-group-item" *ngFor="let car of cars; let i = index | carFilter">' +
    '<b>{{ i + 1 }}</b> {{ car.name}}' +
      '</li>' +
    '</ul>' +
    '</div> '
  //styleUrls: ['./app.component.css']
})
export class AppComponent {
  searchCar = ''
  cars = [
    {name: 'Ford'},
    {name: 'Mazda'},
    {name: 'Bently'},
    {name: 'Audi'},
    {name: 'Mercedes'},
    {name: 'BMW'}
  ]
}
