import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: '<div class="col-xs-8 col-xs-" ',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  headerText = 'Создание своей дерективы';
  items = [1, 2, 3, 4, 5];
  current = 1

  onClick(number: number) {
    this.current = number
  }
}
