import {animate, state, style, transition, trigger} from "@angular/animations";

export const divTtigger = trigger('divTrigger', [
  state('hide', style({
    opacity: 0
  })),
  state('show', style({
    opacity: 1
  })),
  transition('hide => show', [
    
  ])
])
