import { Component } from '@angular/core';

@Component({
  selector: 'app-cars',
  templateUrl: './cars.component.html',
  styleUrls: ['./cars.component.css']
})
export class CarsComponent {


  carName = ''
  carYear = 2017;

    cars : [{ year: number; name: string }, { year: number; name: string }, { year: number; name: string }] = [{
    name: 'Ford',
    year: 2015
  }, {
    name: 'Mazda',
    year: 2010
  }, {
    name: "Audi",
    year: 2017
  }]
  constructor() {

  }

  addCar() {
   this.cars.push({
     name: this.carName,
     year: this.carYear
   });
   this.carName = '',
     this.carYear = 2017

  }
}
