import {animate, state, style, transition, trigger} from "@angular/animations";

export const divTrigger = trigger('divTrigger', [
  state('show', style({
    opacity: 1
  })),
  transition('void => show', [
    style({
      opacity: 0
    }),
    animate(500, style({
      opacity: 1
    }))
  ])
])
