import {Component} from '@angular/core';
import {CarsServise} from "./cars.servise";
import {HttpHeaderResponse, HttpResponse, HttpResponseBase} from "@angular/common/http";

@Component({
  selector: 'app-root',
  templateUrl: `app.component.html`,
  styleUrls: ['./app.component.css']
})
export class AppComponent {

  cars = [];

  constructor(private carsService: CarsServise) { }


  loadCars() {
    this.carsService
      .getCars()
      .subscribe((response) => {
        const data = response.json();
        console.log(data)
      })
  }
}
<div class="col-xs-8 col-xs-offset-2">
  <button class="btn btn-primary" (click)="loadCars()">Загрузить</button>
  <hr>
  <ul class="list-group">
    <li class="list-group-item" *ngFor="let car of cars">
      <!--<span class="badge">{{car.id}}</span>{{car.name}} {{car.color}}-->
    </li>
  </ul>
</div>
